"use strict";
const nav = document.querySelector(".mobile-nav"),
    navMenuBtn = document.querySelector(".nav-menu-btn"),
    navCloseBtn = document.querySelector(".nav-close-btn"),
    navToggleFunc = function() {
        nav.classList.toggle("active")
    };

navMenuBtn.addEventListener("click", navToggleFunc), navCloseBtn.addEventListener("click", navToggleFunc);
const themeBtn = document.querySelectorAll(".theme-btn");
for (let i = 0; i < themeBtn.length; i++) themeBtn[i].addEventListener("click", (function() {
    document.body.classList.toggle("light-theme"), document.body.classList.toggle("dark-theme");
    for (let i = 0; i < themeBtn.length; i++) themeBtn[i].classList.toggle("light"), themeBtn[i].classList.toggle("dark")
}));

// Image Gallery

let slideIndex = 1;
showSlides(slideIndex);

// Next/previous controls
function plusSlides(n) {
  showSlides(slideIndex += n);
}

// Thumbnail image controls
function currentSlide(n) {
  showSlides(slideIndex = n);
}

function showSlides(n) {
  let i;
  let slides = document.getElementsByClassName("mySlides");
  let dots = document.getElementsByClassName("dot");
  if (n > slides.length) {slideIndex = 1}
  if (n < 1) {slideIndex = slides.length}
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";
  }
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }
  slides[slideIndex-1].style.display = "block";
  dots[slideIndex-1].className += " active";
}